const run = (input) => {
    switch(true) {
        case /be|take[\.\s]?be/gi.test(input):
            return 'be';
        case /team|take[\.\s]?team/gi.test(input):
            return 'team';
        case /excellence|take[\.\s]?excellence/gi.test(input):
            return 'excellence';
        case /charge|take[\.\s]?charge/gi.test(input):
            return 'charge';
        case /simple|take[\.\s]?simple/gi.test(input):
            return 'simple';
        case /higher|take[\.\s]?higher/gi.test(input):
            return 'higher';
        case /(fazer\s)?desafio/gi.test(input):
            return 'desafio';
        default:
            return 'Input inesperado';
    }
}

